import 'package:sample_provider_app/models/auth/user.dart';class AuthResponseModel {  bool success;  int statusCode;  String message;  User data;  AuthResponseModel({this.success, this.statusCode, this.message, this.data});  AuthResponseModel.fromJson(Map<String, dynamic> json) {    success = json['success'];    statusCode = json['statusCode'];    message = json['message'];    data = json['data'] != null ? User.fromJson(json['data']) : null;  }  Map<String, dynamic> toJson() {    final Map<String, dynamic> data = Map<String, dynamic>();    data['success'] = this.success;    data['statusCode'] = this.statusCode;    data['message'] = this.message;    if (this.data != null) {      data['data'] = this.data.toJson();    }    return data;  }  AuthResponseModel.withError({bool success, String message, int statusCode}) {    this.success = success;    this.message = message;    this.statusCode = statusCode;  }}