class User {  String id;  String createdAt;  String name;  String avatar;  String mobile;  String email;  String password;  String token;  User(      {this.id,      this.createdAt,      this.name,      this.avatar,      this.mobile,      this.email,      this.password,      this.token});  User.fromJson(Map<String, dynamic> json) {    id = json['id'];    createdAt = json['createdAt'];    name = json['name'];    avatar = json['avatar'];    mobile = json['mobile'];    email = json['email'];    password = json['password'];    token = json['token'];  }  Map<String, dynamic> toJson() {    final Map<String, dynamic> data = new Map<String, dynamic>();    data['id'] = this.id;    data['createdAt'] = this.createdAt;    data['name'] = this.name;    data['avatar'] = this.avatar;    data['mobile'] = this.mobile;    data['email'] = this.email;    data['password'] = this.password;    data['token'] = this.token;    return data;  }}